import React from 'react';
import Drawer from '@mui/material/Drawer';
import TextField from '@mui/material/TextField';
import EditIcon from '@mui/icons-material/Edit';
import InputAdornment from '@mui/material/InputAdornment';
import { TextareaAutosize } from '@mui/base/TextareaAutosize';
import { styled } from '@mui/system';
import VpnKeyIcon from '@mui/icons-material/VpnKey';
import ListaJogadores from '../helpers/ListaJogadores';
import './style.css'
import { listaJogadores1 } from '../helpers/constants';

const DrawerTime = ({ drawerAberto, toggleDrawer  }) => {
  const blue = {
    100: '#DAECFF',
    200: '#b6daff',
    400: '#3399FF',
    500: '#007FFF',
    600: '#0072E5',
    900: '#003A75',
  };

  const grey = {
    50: '#f6f8fa',
    100: '#eaeef2',
    200: '#d0d7de',
    300: '#afb8c1',
    400: '#8c959f',
    500: '#6e7781',
    600: '#57606a',
    700: '#424a53',
    800: '#32383f',
    900: '#24292f',
  };

  const StyledTextarea = styled(TextareaAutosize)(
    ({ theme }) => `
    width: 320px;
    font-family: IBM Plex Sans, sans-serif;
    font-size: 0.875rem;
    font-weight: 400;
    line-height: 1.5;
    padding: 12px;
    max-width: 20vh;
    max-height: 12vh;
    border-radius: 12px 12px 0 12px;
    color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
    background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
    border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
    box-shadow: 0px 2px 2px ${theme.palette.mode === 'dark' ? grey[900] : grey[50]};

    &:hover {
      border-color: ${blue[400]};
    }

    &:focus {
      border-color: ${blue[400]};
      box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[500] : blue[200]};
    }

    // firefox
    &:focus-visible {
      outline: 0;
    }
  `,
  );

  return (
    <Drawer
      anchor="left"
      open={drawerAberto}
      onClose={toggleDrawer(false)}>
      <div id="conteudo-drawer" className="conteudo-drawer">
        <TextField
          focused
          id="nomeGrupo"
          color="primary"
          value="Nome do Grupo"
          label="Nome do Grupo"
          variant="standard"
          sx={{ paddingBottom: '3vh' }}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <EditIcon />
              </InputAdornment>
            ),
          }} />
        <TextField
          focused
          id="codigoGrupo"
          color="primary"
          value="3XH5D8"
          label="Código do Grupo"
          variant="standard"
          sx={{ paddingBottom: '3vh' }}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <VpnKeyIcon />
              </InputAdornment>
            ),
          }} />
        <label className="label-descricao-grupo" htmlFor="descricao">Descrição</label>
        <StyledTextarea
          maxRows={4}
          name="descricao"
          aria-label="maximum height"
          placeholder="Maximum 4 rows"
          defaultValue="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt
            ut labore et dolore magna aliqua." />
        <label className="label-descricao-grupo" htmlFor="descricao">Lista Jogadores:</label>
        <ListaJogadores listaJogadores={listaJogadores1} visualizarJogador={true} />
      </div>
    </Drawer>
  );
};

export default DrawerTime;
